import React from 'react'

export default function SearchFilters(props) {

    const { searchFilters, setSearchFilters } = props

    const handlePriceChange = (e) => {
        const input = e.target.value
        if (e.target.id === "priceInputMin") {
            setSearchFilters(prev => {
                return {
                    ...prev,
                    priceInputMin: input
                }
            })
        } else {
            setSearchFilters(prev => {
                return {
                    ...prev,
                    priceInputMax: input
                }
            })
        }
    }

    // zipcoderadius
    // beds
    // baths
    // sqft
    // price

    return (
        <div id="searchFilterContainer" className="card">

            <h3 id="filterContainerTitle">Filter by:</h3>
            <hr/>

            <p id="priceFilter">
                <button 
                    className="btn btn-light" 
                    type="button" 
                    data-bs-toggle="collapse" 
                    data-bs-target="#collapsePriceFilter" 
                    aria-expanded="false" 
                    aria-controls="collapsePriceFilter"
                >
                    Price
                </button>
            </p>
            <div class="collapse" id="collapsePriceFilter">
                <input id="priceInputMin" className="priceInput" type="text" placeholder="Min" onChange={handlePriceChange}/> to
                <input id="priceInputMax" className="priceInput" type="text" placeholder="Max" onChange={handlePriceChange}/>
            </div>
            <hr/>
        </div>
    )
}
