import React from 'react'
import numeral from 'numeral'

export default function SearchFilters(props) {

    const { searchFilters, setSearchFilters } = props

    const handleChange = (e) => {
        const input = e.target.value

        // price input must be type Number
        const priceRegex = /^[$0-9][0-9,.]*$/
        const isValidNumber = priceRegex.test(input)
        if (isValidNumber) {
            setSearchFilters(prev => {
                return {
                    ...prev,
                    [e.target.id]: input
                }
            })
        }
    }

    function formatNumber(num) {
        return num.toString().replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
      }

    // zipcoderadius
    // beds
    // baths
    // sqft
    // price

    return (
        <div id="searchFilterContainer" className="card">

            <h3 id="filterContainerTitle">Filter by:</h3>
            <hr/>

            <p id="priceFilter">
                <button 
                    className="btn btn-light" 
                    type="button" 
                    data-bs-toggle="collapse" 
                    data-bs-target="#collapsePriceFilter" 
                    aria-expanded="false" 
                    aria-controls="collapsePriceFilter"
                >
                    Price
                </button>
            </p>
            <div className="collapse" id="collapsePriceFilter">
                <input id="priceInputMin" className="priceInput" value={numeral(searchFilters.priceInputMin).format('$0,0.00')} type="text" placeholder="Min" onChange={handleChange}/> to
                <input id="priceInputMax" className="priceInput" value={searchFilters.priceInputMax} type="text" placeholder="Max" onChange={handleChange}/>
            </div>
            <hr/>

            <p id="sqftFilter">
                <button 
                    className="btn btn-light" 
                    type="button" 
                    data-bs-toggle="collapse" 
                    data-bs-target="#collapseSqftFilter" 
                    aria-expanded="false" 
                    aria-controls="collapseSqftFilter"
                >
                    Square Feet
                </button>
            </p>
            <div className="collapse" id="collapseSqftFilter">
                <input id="sqftInputMin" className="sqftInput" value={searchFilters.sqftInputMin} type="text" placeholder="Min" onChange={handleChange}/> to
                <input id="sqftInputMax" className="sqftInput" value={searchFilters.sqftInputMax} type="text" placeholder="Max" onChange={handleChange}/>
            </div>
            <hr/>
        </div>
    )
}
